if(Gp===undefined){var Gp={}}$.extend(Gp,{Slideshow:{addSlide:function(group,id,slide){slide=slide||{};var slideElement=document.getElementById(id);slideElement.className="slide_image";var anchor=document.createElement("A");anchor.href=id+".jpg";anchor.rel="lightbox["+group+"]";anchor.title=slide.description||"";slideElement.appendChild(anchor);if(slide.align==="hidden"){slideElement.style.display="none"}else{var image=document.createElement("IMG");image.title="Click to enlarge";image.src=id+"_tn.jpg";anchor.appendChild(image);var span=document.createElement("SPAN");span.innerHTML=slide.caption||"";slideElement.appendChild(document.createElement("BR"));slideElement.appendChild(span);slideElement.style.float=slide.align||"none"}},fromJSON:function(jsonURL,idPrefix){$.getJSON(jsonURL,function(slides){for(var i=0;i<slides.data.length;i++){var id=idPrefix+"_"+(i+1);Gp.Slideshow.addSlide(slides.group,id,slides.data[i])}})}}});$(function(){Gp.Slideshow.fromJSON("slides.json","slide")});